BEGIN TRANSACTION;

-- Create user table
CREATE TABLE "user" (
    id SERIAL PRIMARY KEY,
    first_name VARCHAR(20) NOT NULL,
    email VARCHAR(120) NOT NULL,
    password VARCHAR(255),
    UNIQUE (email)
);

-- Insert data into user table

-- Create audio_file table
CREATE TABLE audio_file (
    id SERIAL PRIMARY KEY,
    audio_name VARCHAR(50) NOT NULL,
    file_path VARCHAR(100) NOT NULL,
    genre TEXT NOT NULL,
    mood TEXT NOT NULL,
    vocal TEXT NOT NULL
);

-- Insert data into audio_file table
-- Create test table with foreign key constraints
CREATE TABLE test (
    id SERIAL PRIMARY KEY,
    test_type INTEGER NOT NULL,
    test_start_time TIMESTAMP NOT NULL,
    test_end_time TIMESTAMP,
    user_id INTEGER NOT NULL REFERENCES "user"(id),
    pre_survey_data TEXT,
    liked_artists TEXT
);

-- Insert data into test table

CREATE TABLE user_answer (
    id SERIAL PRIMARY KEY,
    overall_rating INTEGER,
    user_id INTEGER NOT NULL REFERENCES "user"(id),
    audio_id INTEGER NOT NULL REFERENCES audio_file(id),
    test_id INTEGER NOT NULL REFERENCES test(id),
    question_index INTEGER NOT NULL
);

-- Insert data into user_answer table
COMMIT;